    private void nextButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nextButtonActionPerformed
		if(sentinel.next==null){
			System.out.println("there are no elements left!");
		}else {
			sentinel=sentinel.next;
			currentLabel.setText(sentinel.toString());
		}
    }//GEN-LAST:event_nextButtonActionPerformed

    private void toTopButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_toTopButtonActionPerformed
		sentinel=pila.node;
		currentLabel.setText(sentinel.toString());
    }//GEN-LAST:event_toTopButtonActionPerformed

    private void popButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_popButtonActionPerformed
		if(pila.isEmpty()){
			System.out.println("the pile is empty!");
			isEmptyView.setText("EMPTY");
		}else{
			topView.setText(pila.node.next.toString());
			pila.pop();
		}
    }//GEN-LAST:event_popButtonActionPerformed

    private void pushButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pushButtonActionPerformed
        if(pila.isEmpty()){
			NodoConc temp = new NodoConc(topArgument.getText(),null);
			pila.node=temp;
			isEmptyView.setText("");
		}else {pila.push(topArgument.getText());}
		sentinel=pila.node;
    }//GEN-LAST:event_pushButtonActionPerformed


